// Extract products from Wayfair's JSON response structure
const inputData = $input.first().json;

console.log('Input data type:', typeof inputData);
console.log('Input data keys:', Object.keys(inputData));

// Parse JSON if it's a string
let jsonData = inputData.data || inputData.body || inputData;
if (typeof jsonData === 'string') {
  try {
    jsonData = JSON.parse(jsonData);
  } catch (e) {
    return [{ json: { error: 'Failed to parse JSON', message: e.message } }];
  }
}

// Handle array format (like your example)
if (Array.isArray(jsonData)) {
  jsonData = jsonData[0];
}

console.log('Working with data structure:', Object.keys(jsonData));

// Extract products from browse.browse_grid_objects
const products = [];

try {
  const browseData = jsonData.browse;
  if (!browseData || !browseData.browse_grid_objects) {
    return [{ json: { error: 'No browse data found', availableKeys: Object.keys(jsonData) } }];
  }

  const gridObjects = browseData.browse_grid_objects;
  console.log(`Found ${gridObjects.length} products in browse_grid_objects`);

  gridObjects.forEach((item, index) => {
    try {
      // Extract basic product info
      const title = item.product_name || '';
      const manufacturer = item.manufacturer || '';
      const sku = item.sku || '';
      const url = item.url || '';

      // Extract pricing information
      let price = '';
      let listPrice = '';
      let salePrice = '';
      let discountPercent = '';

      if (item.raw_pricing_data && item.raw_pricing_data.pricing) {
        const pricing = item.raw_pricing_data.pricing;

        // Get display price
        if (pricing.customerPrice && pricing.customerPrice.display) {
          price = `$${pricing.customerPrice.display.value}`;
          salePrice = price;
        }

        // Get list price  
        if (pricing.listPrice && pricing.listPrice.display) {
          listPrice = `$${pricing.listPrice.display.value}`;
        }

        // Get discount percentage
        if (item.percent_off_amount) {
          discountPercent = `${item.percent_off_amount}%`;
        }
      }

      // Extract image info
      let imageUrl = '';
      if (item.image_data && item.image_data.ireid) {
        imageUrl = `https://assets.wfcdn.com/im/${item.image_data.ireid}/resize-h350-w350%5Ecompr-r85/custom_image.jpg`;
      }

      // Extract reviews
      let rating = '';
      let reviewCount = '';
      if (item.average_overall_rating) {
        rating = item.average_overall_rating.toString();
      }
      if (item.review_count) {
        reviewCount = item.review_count.toString();
      }

      // Extract features/materials
      const features = item.features_array || [];

      // Extract shipping info
      const freeShipping = item.has_free_shipping || false;
      const shippingText = item.free_ship_text || '';

      // Extract size options
      const sizeOptions = item.configured_option_names || [];

      // Only add products with meaningful data
      if (title && title.length > 2) {
        products.push({
          title: title,
          manufacturer: manufacturer,
          sku: sku,
          url: url,
          price: price,
          list_price: listPrice,
          sale_price: salePrice,
          discount_percent: discountPercent,
          image_url: imageUrl,
          rating: rating,
          review_count: reviewCount,
          features: features,
          size_options: sizeOptions,
          free_shipping: freeShipping,
          shipping_text: shippingText,
          scraped_at: new Date().toISOString()
        });
      }
    } catch (e) {
      console.log(`Error processing product ${index}:`, e.message);
    }
  });

  console.log(`Successfully extracted ${products.length} products`);

} catch (e) {
  console.log('Error processing browse data:', e.message);
  return [{ json: { error: 'Failed to process browse data', message: e.message } }];
}

