// Get input JSON
let inputData = $input.first().json;

// Function to clean links (remove trailing commas and whitespace)
function cleanLinks(links) {
    return (links || []).map(l => l.trim().replace(/,+$/, "")).filter(Boolean);
}

// Extract Amazon links: support old 'amazon' array or already separated arrays
let allAmazonLinks = [];
if (inputData.amazon) {
    allAmazonLinks = cleanLinks(inputData.amazon);
} else {
    // If already split arrays exist, merge them
    allAmazonLinks = cleanLinks([...(inputData.amazon_products || []), ...(inputData.amazon_collections || [])]);
}

// Classify links: 
// Products have /dp/ or /gp/product/
// Collections have /gp/bestsellers/ or /s?
let amazon_products = [];
let amazon_collections = [];

allAmazonLinks.forEach(link => {
    if (/\/dp\/|\/gp\/product\//.test(link)) {
        amazon_products.push(link);
    } else if (/\/gp\/bestsellers\/|\/s\?/.test(link)) {
        amazon_collections.push(link);
    }
});

// Return only Amazon products and collections
return [
    {
        json: {
            amazon_products,
            amazon_collections
        }
    }
];

let inputText = $input.first().json.chatInput || $('When chat message received').first().json.chatInput;

// Regex patterns
const walmartRegex = /(https?:\/\/(?:www\.)?walmart\.com\/[^\s]+)/gi;
const amazonRegex = /(https?:\/\/(?:www\.)?amazon\.[a-z.]+\/[^\s]+)/gi;

// Extract all links
let walmartLinks = inputText.match(walmartRegex) || [];
let amazonLinks = inputText.match(amazonRegex) || [];

// Function to classify links
function classifyLinks(links, productPattern, collectionPattern) {
    let products = [];
    let collections = [];
    for (let link of links) {
        if (productPattern.test(link)) {
            products.push(link);
        } else if (collectionPattern.test(link)) {
            collections.push(link);
        } else {
            // Unknown type, you can log or ignore
        }
    }
    return { products, collections };
}

// Walmart: products have /ip/, collections /search?q=/ or /browse/
let walmartClassified = classifyLinks(walmartLinks, /\/ip\//, /\/search\?q=|\/browse\//);

// Amazon: products have /dp/ or /gp/product/, collections /gp/bestsellers/ or /s?
let amazonClassified = classifyLinks(amazonLinks, /\/dp\/|\/gp\/product\//, /\/gp\/bestsellers\/|\/s\?/);

// Return grouped arrays
return [
    {
        json: {
            walmart_products: walmartClassified.products,
            walmart_collections: walmartClassified.collections,
            amazon_products: amazonClassified.products,
            amazon_collections: amazonClassified.collections
        }
    }
];

