let html = $input.first().json.output;

// Remove markdown fences like ```html or ```
html = html.replace(/```html|```/g, "");

// Remove escaped newline/tab characters and excess whitespace
html = html.replace(/\\[nrt]/g, " ").replace(/\s{2,}/g, " ");

// Convert basic markdown syntax inside text to HTML
html = html
  // Bold **text** or __text__
  .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
  .replace(/__(.*?)__/g, "<strong>$1</strong>")
  // Italic *text* or _text_
  .replace(/(?<!\*)\*(?!\*)(.*?)\*(?<!\*)/g, "<em>$1</em>")
  .replace(/_(.*?)_/g, "<em>$1</em>")
  // Headings ##, ###, etc. (optional)
  .replace(/^###\s*(.*)$/gm, "<h3>$1</h3>")
  .replace(/^##\s*(.*)$/gm, "<h2>$1</h2>")
  .replace(/^#\s*(.*)$/gm, "<h1>$1</h1>")
  // Bullet points or dashes
  .replace(/^\s*[-*]\s+(.*)$/gm, "<li>$1</li>")
  .replace(/(<li>.*<\/li>)/gs, "<ul>$1</ul>");

// Remove duplicate or misplaced <html>/<body> wrappers
html = html
  .replace(/<\/?body><\/?body>/g, "")
  .replace(/<\/?html><\/?html>/g, "")
  .replace(/<body><!DOCTYPE html>/g, "<!DOCTYPE html>");

// Trim leading/trailing spaces
html = html.trim();

// Ensure it’s wrapped in a proper HTML structure
if (!html.startsWith("<!DOCTYPE html>")) {
  html = `<!DOCTYPE html><html><body>${html}</body></html>`;
}

// Return clean, formatted HTML
return [{ json: { cleaned_minified_html: html } }];

